public class task2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 0; i < 50; i++)	{
			if(i % 2 == 1) {
				System.out.println(i);
			}
		}
		
		System.out.println();
		
		for(int j = 51 ; j <= 100; j++)	{
			if(j % 2 == 0) {
				System.out.println(j);
			}
		}

	}

}
